mapper.select:
		select 
			id,
			create_date,
			modify_date,
			code,
			name,
			status,
			person,
			phone,
			mobile,
			email,
			website,
			address,
			star_level,
			property,
			province_code,
			city_code,
			district_code,
			note
		from organization_info
		where 1=1 

				and id=#{id}


				and create_date=#{createDate}


				and modify_date=#{modifyDate}


				and code=#{code}


				and name=#{name}


				and status=#{status}


				and person=#{person}


				and phone=#{phone}


				and mobile=#{mobile}


				and star_level=#{starLevel}


				and property=#{property}


            limit #{pageUtil.startRow},#{pageUtil.endRow}

select
    count(*)
from organization_info
where 1=1

    and id=#{id}


    and create_date=#{createDate}


    and modify_date=#{modifyDate}


    and code=#{code}


    and name=#{name}


    and status=#{status}


    and person=#{person}


    and phone=#{phone}


    and mobile=#{mobile}


    and star_level=#{starLevel}


    and property=#{property}
