mapper.select:
select vi.id, vi.create_date, vi.modify_date, vi.code, vi.name, vi.vip_type_code, vt.name as vip_type_name, vi.sex, vi.shop_code, oi.name as shop_name, vi.tel, vi.phone, vi.birthday, vi.identity_card, vi.issuing_person, vi.province_code, rp.region_name as province_name, vi.city_code, rc.region_name as city_name, vi.district_code, rd.region_name as district_name, vi.post, vi.email, vi.address, vi.qq, vi.del, vi.pwd, vi.note from vip_info vi LEFT JOIN region rp on rp.region_id = vi.province_code LEFT JOIN region rc on rc.region_id = vi.city_code LEFT JOIN region rd on rd.region_id = vi.district_code LEFT JOIN organization_info oi on oi.code = vi.shop_code LEFT JOIN vip_type vt on vt.code = vi.vip_type_code where 1=1
and vi.id=#{id}
and vi.create_date=#{createDate}
and vi.code=#{code}
and vi.name=#{name}
and vi.vip_type_code=#{vipTypeCode}
and vi.sex=#{sex}
and shop_code=#{shopCode}
and vi.shop_name=#{shopName}
and vi.tel=#{tel}
and vi.phone=#{phone}
and vi.identity_card=#{identityCard}
and vi.qq=#{qq}
and vi.del=#{del}
limit #{pageUtil.startRow},#{pageUtil.endRow}

select
count(*)
from vip_info vi
LEFT JOIN region rp on rp.region_id = vi.province_code
LEFT JOIN region rc on rc.region_id = vi.city_code
LEFT JOIN region rd on rd.region_id = vi.district_code
LEFT JOIN organization_info oi on oi.code = vi.shop_code
where 1=1

    and vi.id=#{id}


    and vi.create_date=#{createDate}


    and vi.code=#{code}


    and vi.name=#{name}


    and vi.vip_type_code=#{vipTypeCode}


    and vi.sex=#{sex}


    and shop_code=#{shopCode}


    and vi.shop_name=#{shopName}


    and vi.tel=#{tel}


    and vi.phone=#{phone}


    and vi.identity_card=#{identityCard}


    and vi.qq=#{qq}


    and vi.del=#{del}
