mapper.select:
        SELECT
        count(*)
        from shop_book_detail
        where true

            and flow_no = #{flowNo}


            and sale_type = #{saleType}



			  and DATE_FORMAT(sale_date, '%Y-%m-%d') >=  DATE_FORMAT(#{startDate}, '%Y-%m-%d')




			  and DATE_FORMAT(sale_date, '%Y-%m-%d') <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')



            and is_del = #{isDel}

SELECT id, flow_no, sale_date, vip_no, case is_online when 0 then '否' when 1 then '是' end as is_online_name, case sale_type when 1 then '销售' when 2 then '退货' end as sale_type_name, fund_amount, note, create_user, create_date, modify_date, modify_user from shop_book_detail where true
and flow_no = #{flowNo}
and sale_type = #{saleType}
and DATE_FORMAT(sale_date, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')
and DATE_FORMAT(sale_date, '%Y-%m-%d') <= DATE_FORMAT(#{endDate}, '%Y-%m-%d')
and is_del = #{isDel}
order by sale_date desc
limit #{pageUtil.startRow},#{pageUtil.endRow}
