mapper.select:
select
    ps.id,
    ps.pid,
    ps.product_code,
    p.name as product_name,
    ps.code,
    ps.name,
    ps.gb_code,
    ps.unt_price,
    ps.cost_price,
    ps.use_price,
    ps.del,
    ps.memo
from product_sku ps
left join product p on p.code = ps.product_code
where 1=1

    and ps.id=#{id}


    and ps.pid=#{pid}


    and ps.product_code=#{productCode}


    and ps.code=#{code}


    and ps.name=#{name}


    and ps.gb_code=#{gbCode}


    and ps.del=#{del}


    limit #{pageUtil.startRow},#{pageUtil.endRow}

select
count(*)
from product_sku ps
left join product p on p.code = ps.product_code
where 1=1

    and ps.id=#{id}


    and ps.pid=#{pid}


    and ps.product_code=#{productCode}


    and ps.code=#{code}


    and ps.name=#{name}


    and ps.gb_code=#{gbCode}


    and ps.del=#{del}
