mapper.select:
select 
	id,
	create_date,
	modify_date,
	warehouse_code,
	product_code,
	sku_code,
	qty,
	del
from inventory
where 1=1 

		and id=#{id}


		and warehouse_code=#{warehouseCode}


		and product_code=#{productCode}


		and sku_code=#{skuCode}


		and del=#{del}

select
count(*)
from inventory
where 1=1

    and id=#{id}


    and warehouse_code=#{warehouseCode}


    and product_code=#{productCode}


    and sku_code=#{skuCode}


    and del=#{del}

mapper.update:
		update inventory


                modify_date=now(),


                qty=#{qty},


                del=#{del}


        where product_code = #{productCode} and sku_code = #{skuCode} and warehouse_code = #{warehouseCode}
