mapper.select:
select pem.id, pem.bill_no, pem.bill_date, pem.purchase_entry_type_code, pet.name as purchase_entry_type_name, pem.org_code, oi.name as org_name, pem.warehouse_code, wh.name as warehouse_name, pem.supplier_info_code, si.name as supplier_info_name, pem.purchase_order_main_id, pem.status, pem.create_user, pem.create_date, pem.modify_user, pem.modify_date, pem.audit_user, pem.audit_date, pem.fa_audit_user, pem.fa_audit_date, pem.cancel_user, pem.cancel_date, pem.account_user, pem.account_date, pem.note from purchase_entry_main pem LEFT JOIN organization_info oi on oi.code = pem.org_code LEFT JOIN supplier_info si on si.code = pem.supplier_info_code LEFT JOIN warehouse wh on wh.code = pem.warehouse_code LEFT JOIN purchase_entry_type pet on pet.code = pem.purchase_entry_type_code where 1=1
and pem.id=#{id}
and pem.bill_no=#{billNo}
and pem.bill_date=#{billDate}
and pem.purchase_entry_type_code=#{purchaseEntryTypeCode}
and pem.org_code=#{orgCode}
and pem.warehouse_code=#{warehouseCode}
and pem.supplier_info_code=#{supplierInfoCode}
and pem.purchase_order_main_id=#{purchaseOrderMainId}
and pem.status=#{status}
order by pem.id desc
limit #{pageUtil.startRow},#{pageUtil.endRow}

select
    count(*)
from purchase_entry_main pem
LEFT JOIN organization_info oi on oi.code = pem.org_code
LEFT JOIN supplier_info si on si.code = pem.supplier_info_code
LEFT JOIN warehouse wh on wh.code = pem.warehouse_code
LEFT JOIN purchase_entry_type pet on pet.code = pem.purchase_entry_type_code
where 1=1

    and pem.id=#{id}


    and pem.bill_no=#{billNo}


    and pem.bill_date=#{billDate}


    and pem.purchase_entry_type_code=#{purchaseEntryTypeCode}


    and pem.org_code=#{orgCode}


    and pem.warehouse_code=#{warehouseCode}


    and pem.supplier_info_code=#{supplierInfoCode}


    and pem.purchase_order_main_id=#{purchaseOrderMainId}


    and pem.status=#{status}
