mapper.select:
select pom.id, pom.bill_no, pom.bill_date, pom.purchase_type_code, pt.name as purchase_type_name, pom.org_code, oi.name as org_name, pom.warehouse_code, wh.name as warehouse_name, pom.supplier_info_code, si.name as supplier_info_name, pom.status, pom.create_user, pom.create_date, pom.modify_user, pom.modify_date, pom.audit_user, pom.audit_date, pom.cancel_user, pom.cancel_date, pom.note from purchase_order_main pom LEFT JOIN purchase_type pt on pt.code = pom.purchase_type_code LEFT JOIN organization_info oi on oi.code = pom.org_code LEFT JOIN warehouse wh on wh.code = pom.warehouse_code LEFT JOIN supplier_info si on si.code = pom.supplier_info_code where 1=1
and pom.id=#{id}
and pom.bill_no=#{billNo}
and pom.bill_date=#{billDate}
and pom.purchase_type_code=#{purchaseTypeCode}
and pom.org_code=#{orgCode}
and pom.warehouse_code=#{warehouseCode}
and pom.supplier_info_code=#{supplierInfoCode}
and pom.status=#{status}
limit #{pageUtil.startRow},#{pageUtil.endRow}

select
    count(*)
from purchase_order_main pom
LEFT JOIN purchase_type pt on pt.code = pom.purchase_type_code
LEFT JOIN organization_info oi on oi.code = pom.org_code
LEFT JOIN warehouse wh on wh.code = pom.warehouse_code
LEFT JOIN supplier_info si on si.code = pom.supplier_info_code
where 1=1

    and pom.id=#{id}


    and pom.bill_no=#{billNo}


    and pom.bill_date=#{billDate}


    and pom.purchase_type_code=#{purchaseTypeCode}


    and pom.org_code=#{orgCode}


    and pom.warehouse_code=#{warehouseCode}


    and pom.supplier_info_code=#{supplierInfoCode}


    and pom.status=#{status}
